mapper.select:
		select  from msg_info mi inner join msg_rece mr on(mi.id=mr.msg_id)

			mr.rece_sys_no=#{sysNo}
			and mr.rece_user_id=#{userId}
			and mr.is_read=#{isRead}



			order by 
				${item.property} ${item.type}


		order by mi.send_time desc

  		limit #{currentIndex}, #{size}

select count(*) from msg_info mi inner join msg_rece mr on(mi.id=mr.msg_id)

	mr.rece_sys_no=#{sysNo}
	and mr.rece_user_id=#{userId}
	and mr.is_read=#{isRead}
